import React from "react";
import Transaction from "./Transaction";

function TransactionsList() {
  // Assume you have transaction data, replace this with your actual data
  const transactionsData = [
    { id: 1, date: "2023-01-01", description: "Payment", category: "Income", amount: 100 },
    { id: 2, date: "2023-01-02", description: "Groceries", category: "Expenses", amount: -50 },
    // Add more transactions as needed
  ];

  return (
    <table className="ui celled striped padded table">
      <tbody>
        <tr>
          <th>
            <h3 className="ui center aligned header">Date</h3>
          </th>
          <th>
            <h3 className="ui center aligned header">Description</h3>
          </th>
          <th>
            <h3 className="ui center aligned header">Category</h3>
          </th>
          <th>
            <h3 className="ui center aligned header">Amount</h3>
          </th>
        </tr>
        {transactionsData.map((transaction) => (
          <Transaction
            key={transaction.id}
            date={transaction.date}
            description={transaction.description}
            category={transaction.category}
            amount={transaction.amount}
          />
        ))}
      </tbody>
    </table>
  );
}

export default TransactionsList;
