// Transaction.js

function Transaction({ id, date, description, category, amount, onDelete }) {
  const handleDelete = () => {
    onDelete(id);
  };

  return (
    <tr>
      <td>{date}</td>
      <td>{description}</td>
      <td>{category}</td>
      <td>{amount}</td>
      <td>
        <button onClick={handleDelete}>Delete</button>
      </td>
    </tr>
  );
}

export default Transaction;
