// TransactionsList.js

import React, { useEffect, useState } from "react";
import Transaction from "./Transaction";
import AddTransactionForm from "./AddTransactionForm";

function TransactionsList() {
  const [transactionsData, setTransactionsData] = useState([]);

  useEffect(() => {
    // Fetch data from the provided JSON server
    fetch("http://localhost:8001/transactions")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTransactionsData(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, []);

  const addTransaction = (newTransaction) => {
    // Post the new transaction to the backend API for persistence
    fetch("http://localhost:8001/transactions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newTransaction),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        // Update the local state with the new transaction
        setTransactionsData([...transactionsData, data]);
        console.log("New transaction added:", data);
      })
      .catch((error) => {
        console.error("Error adding new transaction:", error);
      });
  };

  return (
    <div>
      <AddTransactionForm onAddTransaction={addTransaction} />
      <table className="ui celled striped padded table">
        <tbody>
          <tr>
            <th>
              <h3 className="ui center aligned header">Date</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Description</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Category</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Amount</h3>
            </th>
          </tr>
          {transactionsData.map((transaction) => (
            <Transaction
              key={transaction.id}
              date={transaction.date}
              description={transaction.description}
              category={transaction.category}
              amount={transaction.amount}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionsList;
