// TransactionsList.js
import React, { useEffect, useState, useCallback } from "react";
import Transaction from "./Transaction";
import AddTransactionForm from "./AddTransactionForm";

function TransactionsList({ handleSearch, searchTerm }) {
  const [transactionsData, setTransactionsData] = useState([]);
  const [filteredTransactions, setFilteredTransactions] = useState([]);
  const [sortCriteria, setSortCriteria] = useState(null);

  const applySearchFilter = useCallback(
    (data) => {
      const filtered = data.filter((transaction) =>
        transaction.description.toLowerCase().includes(searchTerm.toLowerCase())
      );
      setFilteredTransactions(filtered);
    },
    [searchTerm]
  );

  const applySort = useCallback(
    (data) => {
      if (sortCriteria === "category") {
        data.sort((a, b) => a.category.localeCompare(b.category));
      } else if (sortCriteria === "description") {
        data.sort((a, b) => a.description.localeCompare(b.description));
      }
      setFilteredTransactions([...data]);
    },
    [sortCriteria]
  );

  useEffect(() => {
    // Fetch data from the provided JSON server
    fetch("http://localhost:8001/transactions")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTransactionsData(data);
        applySearchFilter(data);
        applySort(data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [searchTerm, applySearchFilter, applySort]); // Include applySearchFilter and applySort in the dependency array

  const addTransaction = (newTransaction) => {
    // Post the new transaction to the backend API for persistence
    fetch("http://localhost:8001/transactions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newTransaction),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! Status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setTransactionsData([...transactionsData, data]); // Update the local state with the new transaction
        applySearchFilter([...transactionsData, data]); // Update filtered transactions as well
        applySort([...transactionsData, data]); // Update sorted transactions as well
        console.log("New transaction added:", data);
      })
      .catch((error) => {
        console.error("Error adding new transaction:", error);
      });
  };

  const handleSortChange = (criteria) => {
    setSortCriteria(criteria);
    applySort(filteredTransactions);
  };

  return (
    <div>
      <AddTransactionForm onAddTransaction={addTransaction} />
      <div>
        <label>Sort By: </label>
        <select onChange={(e) => handleSortChange(e.target.value)}>
          <option value="">--Select--</option>
          <option value="category">Category</option>
          <option value="description">Description</option>
        </select>
      </div>
      <table className="ui celled striped padded table">
        <tbody>
          <tr>
            <th>
              <h3 className="ui center aligned header">Date</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Description</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Category</h3>
            </th>
            <th>
              <h3 className="ui center aligned header">Amount</h3>
            </th>
          </tr>
          {filteredTransactions.map((transaction) => (
            <Transaction
              key={transaction.id}
              date={transaction.date}
              description={transaction.description}
              category={transaction.category}
              amount={transaction.amount}
            />
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default TransactionsList;
